import axios from "axios";

const BASE_URL =
  "mongodb+srv://driven:<123>@mywallet.dbyxv.mongodb.net/myWallet?retryWrites=true&w=majority";

// function createConfig(token) {
//   return { headers: { Authorization: `Bearer ${token}` } };
// }

function signUp(formData) {
  const promise = axios.post(`${BASE_URL}/auth/sign-up`, formData);
  return promise;
}
function login(formData) {
  const promise = axios.post(`${BASE_URL}/auth/login`, formData);
  return promise;
}

function addWithdraw(data, token) {
  const promise = axios.post(`${BASE_URL}/home/withdraw`, data, token);
  return promise;
}

function addDeposit(data, token) {
  const promise = axios.post(`${BASE_URL}/home/deposit`, data, token);
  return promise;
}

function deletedHabit(id, token) {
  const promise = axios.delete(`${BASE_URL}/habits/${id}`, token);
  return promise;
}

function markHabit(id, done, token) {
  if (done === false) {
    const promise = axios.post(`${BASE_URL}/habits/${id}/check`, done, token);
    return promise;
  } else {
    const promise = axios.post(`${BASE_URL}/habits/${id}/uncheck`, done, token);
    return promise;
  }
}

export { signUp, login, addWithdraw, addDeposit, deletedHabit, markHabit };
