import { useState, useEffect, useContext } from "react";
import { Container, Statement } from "./style";
import { useNavigate } from "react-router-dom";
import Top from "../Top";
import Menu from "../Menu";
import UserContext from "../../contexts/UserContext";
import { getTransaction } from "../../services/mywallet";

export default function HomePage() {
  const [records, setRecords] = useState([]);
  const { user, setUser } = useContext(UserContext);
  const [total, setTotal] = useState(0);
  const config = { headers: { Authorization: `Bearer ${user.token}` } };
  const navigate = useNavigate();

  useEffect(() => {
    renderHomePage();
  }, []);

  function renderHomePage() {
    const promise = getTransaction(config);
    promise.then((response) => {
      setRecords(response.data);
      calcTotal();
    });
  }

  function calcTotal() {
    const depositValue = records.filter((t) => t.type === "deposit").value;
    const withdrawValue = records.filter((t) => t.type === "withdraw").value;
    let totalDeposit = 0;
    let totalWithdraw = 0;

    for (const value of depositValue) {
      totalDeposit += value;
    }
    for (const value of withdrawValue) {
      totalWithdraw += value;
    }
    setTotal(totalDeposit - totalWithdraw);
  }

  return (
    <>
      <Container>
        <Top />
        <Statement>
          {records.reverse().map((record) => (
            <div type={record.type} key={record._id}>
              <span>{record.date}</span>

              <span className="amount">
                {parseFloat(record.addValue).toFixed(2)}
              </span>
            </div>
          ))}
        </Statement>
        <Menu />
      </Container>
    </>
  );
}
