import { useState, useEffect, useContext } from "react";
import { Container, Statement } from "./style";
import { useNavigate } from "react-router-dom";
import Top from "../Top";
import Menu from "../Menu";
import UserContext from "../../contexts/UserContext";
import { getTransaction } from "../../services/mywallet";

export default function HomePage() {
  const [records, setRecords] = useState([]);
  const { token } = useContext(UserContext);
  const auth = { headers: { Authorization: `Bearer ${token}` } };
  const navigate = useNavigate();

  let total = 0;

  useEffect(() => {
    renderHomePage();
  }, []);

  if (records === "") {
    return (
      <>
        <Container>
          <Top />
          <Statement>
            <div className="center">
              <span>Não há registros de entrada ou saída</span>
            </div>
          </Statement>
          <Menu />
        </Container>
      </>
    );
  }

  for (const record of records) {
    if (record.type === "deposit") total += parseFloat(record.addValue);
    else total -= parseFloat(record.addValue);
  }

  function renderHomePage() {
    const promise = getTransaction(auth);
    promise.then((response) => {
      setRecords(response.data);

      promise.catch((error) => console.log(error.response));
    });
  }

  return (
    <>
      <Container>
        <Top />
        <Statement>
          {records.reverse().map((record) => (
            <div type={record.type} key={record._id}>
              <span>{record.date}</span>

              <span className="amount">
                {parseFloat(record.addValue).toFixed(2)}
              </span>
            </div>
          ))}
        </Statement>
        <Menu />
      </Container>
    </>
  );
}
