import axios from "axios";

function createConfig(token) {
  return { headers: { Authorization: `Bearer ${token}` } };
}

function signUp(formData) {
  const promise = axios.post(
    `${process.env.REACT_APP_API}/auth/sign-up`,
    formData
  );
  return promise;
}
function login(formData) {
  const promise = axios.post(
    `${process.env.REACT_APP_API}/auth/login`,
    formData
  );
  return promise;
}

function addWithdraw(data, token) {
  const promise = axios.post(
    `${process.env.REACT_APP_API}/home/withdraw`,
    data,
    token
  );
  return promise;
}

function addDeposit(data, token) {
  const promise = axios.post(
    `${process.env.REACT_APP_API}/home/deposit`,
    data,
    token
  );
  return promise;
}

function getTransaction(token) {
  const promise = axios.delete(`${process.env.REACT_APP_API}/home`, token);
  return promise;
}

function markHabit(id, done, token) {
  if (done === false) {
    const promise = axios.post(
      `${process.env.REACT_APP_API}/habits/${id}/check`,
      done,
      token
    );
    return promise;
  } else {
    const promise = axios.post(
      `${process.env.REACT_APP_API}/habits/${id}/uncheck`,
      done,
      token
    );
    return promise;
  }
}

export { signUp, login, addWithdraw, addDeposit, getTransaction, markHabit };
