            disabled={isLoading}
            type="text"
            onChange={(e) => setDescription(e.target.value)}
            value={description}
            placeholder="Descrição"
          ></Input>

          <Button disabled={isLoading} type="submit">
            {isLoading ? <Loading /> : "Salvar entrada"}
          </Button>
        </form>
      </Container>
    </>
  );
}
