import styled from "styled-components";

const Container = styled.div`
  min-height: 100vh;

  margin-left: 30px;
  margin-right: 30px;

  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 16px;
`;
const Statement = styled.div`
  width: 326px;
  height: 446px;
  background-color: white;
  border-radius: 5px;
  display: flex;
  justify-content: center;
  align-items: center;
  .center {
    width: 180px;
    height: 50px;
    display: flex;
    align-items: center;
  }
  span {
    font-size: 20px;
    line-height: 23px;
    text-align: center;
    color: #868686;
  }
`;

export { Container, Statement };
