import { useState, useEffect, useContext } from "react";
import { Container, Statement, Total } from "./style";
import Top from "../Top";
import Menu from "../Menu";
import Money from "../Money";
import UserContext from "../../contexts/UserContext";
import { getTransaction } from "../../services/mywallet";

export default function HomePage() {
  const [formData, setFormData] = useState([]);
  const { user } = useContext(UserContext);
  const [userTotal, setUserTotal] = useState(0);
  const config = { headers: { Authorization: `Bearer ${user.token}` } };

  useEffect(() => {
    renderHomePage();
  }, []);

  // useEffect([records]);

  function renderHomePage() {
    const promise = getTransaction(config);
    promise.then((response) => {
      setFormData(response.data);

      let totalDeposit = 0;
      let totalWithdraw = 0;

      for (let i = 0; i < formData.length; i++) {
        if (formData[i].type === "deposit") {
          const depositValue = formData[i].value;
          totalDeposit += parseFloat(depositValue);
          console.log(depositValue[i].value);
        } else {
          const withdrawValue = formData[i].value;
          // console.log(withdrawValue);
        }
      }
      // const depositValue = records.filter((r) => r.type === "deposit").value;
      // const withdrawValue = records.filter((r) => r.type === "withdraw").value;
      // console.log(depositValue);
      // console.log(withdrawValue);
      // console.log(records);
      // for (let i = 0; i < depositValue.length; i++) {
      //   totalDeposit += parseFloat(depositValue[i].value);
      // }
      // for (let i = 0; i < withdrawValue.length; i++) {
      //   totalWithdraw += parseFloat(withdrawValue[i].value);
      // }
      // setUserTotal((totalDeposit - totalWithdraw).toFixed(2));
    });
  }

  return (
    <>
      <Container>
        <Top />
        <Statement>
          {formData.length === 0 ? (
            <div className="center">
              <span>Não há registros de entrada ou saída</span>
            </div>
          ) : (
            formData.map((r, i) => <Money money={r} key={i} />)
          )}
          <Total>
            {" "}
            Saldo{" "}
            <span className={userTotal >= 0 ? "green" : "red"}>
              {userTotal}
            </span>
          </Total>
        </Statement>
        <Menu />
      </Container>
    </>
  );
}
