import { useState, useEffect, useContext } from "react";
import { Container, Statement, Total } from "./style";
import Top from "../Top";
import Menu from "../Menu";
import Money from "../Money";
import UserContext from "../../contexts/UserContext";
import { getTransaction } from "../../services/mywallet";

export default function HomePage() {
  const [records, setRecords] = useState([]);
  const { user } = useContext(UserContext);
  const [userTotal, setUserTotal] = useState(0);
  const config = { headers: { Authorization: `Bearer ${user.token}` } };

  useEffect(() => {
    renderHomePage();
  }, []);

  useEffect(calcTotal, [records]);

  function renderHomePage() {
    const promise = getTransaction(config);
    promise.then((response) => {
      setRecords(response.data);
      calcTotal();
    });
  }

  function calcTotal() {
    const depositValue = records.filter((r) => r.type === "deposit").value;
    const withdrawValue = records.filter((r) => r.type === "withdraw").value;
    let totalDeposit = 0;
    let totalWithdraw = 0;

    for (let i = 0; i < depositValue.length; i++) {
      totalDeposit += parseFloat(depositValue[i].value);
    }
    for (let i = 0; i < withdrawValue.length; i++) {
      totalWithdraw += parseFloat(withdrawValue[i].value);
    }
    setUserTotal(totalDeposit - totalWithdraw);
  }

  return (
    <>
      <Container>
        <Top />
        <Statement>
          {records.length === 0 ? (
            <div className="center">
              <span>Não há registros de entrada ou saída</span>
            </div>
          ) : (
            records.map((t, i) => <Money money={t} key={i} />)
          )}
          <Total>
            {" "}
            Saldo{" "}
            <span className={userTotal >= 0 ? "green" : "red"}>
              {userTotal}
            </span>
          </Total>
        </Statement>
        <Menu />
      </Container>
    </>
  );
}
